import firebase from "firebase/app"
import database from "firebase/database";

const firebaseConfig = {
    apiKey: "AIzaSyAsD-0GKArCdjRpopHQ1M6ScAESz9Th0AY",
    authDomain: "meet-clone-c1e00.firebaseapp.com",
    databaseURL: "https://meet-clone-c1e00-default-rtdb.asia-southeast1.firebasedatabase.app",
    projectId: "meet-clone-c1e00",
    storageBucket: "meet-clone-c1e00.appspot.com",
    messagingSenderId: "109367301966",
    appId: "1:109367301966:web:094ce1efbe70346c73301f"
  };

firebase.initializeApp(firebaseConfig);

let dbRef = firebase.database().ref();

export const userName = prompt("what's your name?");

const urlParams = new URLSearchParams(window.location.search);
const roomId = urlParams.get("id");

if (roomId) {
    dbRef = dbRef.child(roomId);
}else {
    dbRef = dbRef.push();
    window.history.replaceState(null, "Meet", "?id=" + dbRef.key);
}

export default dbRef;
